import { Component, OnInit } from "@angular/core";
@Component({
    templateUrl: "./test.component.html"
})
export class TestComponent implements OnInit{
    ngOnInit(): void {
        
    }
    now: number = 0; // Biến để điều khiển câu hỏi hiện tại
  totalSteps: number = 5;
  selectedOptions: any = {}; // Lưu trữ các giá trị của radio đã chọn
  errorMsg: string = '';
  showResultPage: boolean = false;
  userScore: number = 0;
  passingScore: number = 80;

  // Nội dung câu hỏi được quản lý trong mảng
  steps = [
    {
      id: 1,
      question: 'Which former British colony was given back to China in 1997?',
      options: ['Russia', 'America', 'Australia', 'Hong Kong']
    },
    {
      id: 2,
      question: 'For which of the following disciplines is Nobel Prize awarded?',
      options: ['Physics and Chemistry', 'Physiology or Medicine', 'Literature Economics', 'All of the above']
    },
    {
      id: 3,
      question: 'Fire temple is the place of worship of which of the following religion?',
      options: ['Taoism', 'Judaism', 'Zoroastrianism', 'Shintoism']
    },
    {
      id: 4,
      question: 'Who was the first Indian Commander-in-Chief (C-in-C) of the Indian Army?',
      options: ['Gen. K.M. Cariappa', 'Vice-Admiral R.D. Katari', 'Rajendra Singhji', 'None of the above']
    },
    {
      id: 5,
      question: 'For galvanizing iron which of the following metals is used?',
      options: ['Aluminium', 'Copper', 'Lead', 'Zinc']
    }
  ];

  // Hiển thị câu hỏi hiện tại
  getCurrentStep() {
    return this.steps[this.now];
  }

  // Xác thực xem có chọn câu trả lời không
  validate(stepId: number): boolean {
    if (!this.selectedOptions[stepId]) {
      this.errorMsg = 'Choose an option!';
      return false;
    }
    this.errorMsg = ''; // Reset lại lỗi nếu có
    return true;
  }

  // Chuyển đến câu tiếp theo
  nextStep() {
    if (this.validate(this.now + 1)) {
      this.now = this.now + 1;
    }
    if (this.now === this.totalSteps) {
      this.calculateScore();
    }
  }

  // Trở lại câu trước
  prevStep() {
    if (this.now > 0) {
      this.now = this.now - 1;
    }
  }

  // Tính điểm sau khi hoàn tất
  calculateScore() {
    // Tính điểm dựa trên các câu trả lời đã chọn
    this.userScore = 20; // Đây là ví dụ
    this.showResultPage = true;
  }

  // Phương thức chọn radio option
  onOptionSelected(stepId: number, option: string) {
    this.selectedOptions[stepId] = option;
  }

  // Kiểm tra xem kết quả có đạt hay không
  isPassed() {
    return this.userScore >= this.passingScore;
  }
}
