import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-code',
  templateUrl: './code.component.html',
  styleUrls: ['./code.component.css']
})
export class CodeComponent {
  testCode: string = '';
 

  constructor(private router: Router) {}

  onSubmitCode() {
    // Kiểm tra mã code có hợp lệ không
    if (this.testCode === this.validCode) {
      // Chuyển đến trang quiz nếu đúng mã code
      this.router.navigate(['/quiz']);
    } else {
      alert('Invalid test code. Please try again.');
    }
  }
}
<div class="code-container">
  <div class="code-box">
    <h2>Enter Test Code</h2>
    <form (ngSubmit)="onSubmitCode()">
      <div class="input-box">
        <input type="text" name="testCode" required [(ngModel)]="testCode" placeholder="Enter your test code" />
      </div>
      <div class="button-box">
        <button type="submit" class="submit-btn">Submit Code</button>
      </div>
    </form>
  </div>
</div>
