import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { Test } from "src/app/models/test.model";
import { TestService } from "src/app/services/test.service";
@Component({
    templateUrl: "./test.component.html"
})
export class TestComponent implements OnInit{
  
   
    now: number = 0; // Biến để điều khiển câu hỏi hiện tại
    totalSteps: number;
    selectedOptions: any = {}; // Lưu trữ các giá trị đã chọn cho các câu hỏi có đáp án
    textAnswers: any = {}; // Lưu trữ câu trả lời cho các câu hỏi dạng textAnswer
    errorMsg: string = '';
    showResultPage: boolean = false;
    userScore: number = 0; // Lưu điểm số của người dùng
    maxScore: number = 0; // Tổng điểm tối đa có thể đạt được
    passingScore: number = 80; // Mức điểm để đậu (pass)
    code: string;
    test: Test;
    title: string;
    // Nội dung câu hỏi được quản lý trong mảng
    steps = [
      {
        id: 1,
        testID: 101,
        questionType: 'choiceAnswer',
        content: 'Which former British colony was given back to China in 1997?',
        answers: [
          { id: 1, content: 'Russia', isCorrect: false },
          { id: 2, content: 'America', isCorrect: false },
          { id: 3, content: 'Australia', isCorrect: false },
          { id: 4, content: 'Hong Kong', isCorrect: true }
        ]
      },
      {
        id: 2,
        testID: 101,
        questionType: 'choiceAnswer',
        content: 'For which of the following disciplines is Nobel Prize awarded?',
        answers: [
          { id: 1, content: 'Physics and Chemistry', isCorrect: true },
          { id: 2, content: 'Physiology or Medicine', isCorrect: false },
          { id: 3, content: 'Literature Economics', isCorrect: false },
          { id: 4, content: 'All of the above', isCorrect: false }
        ]
      },
      {
        id: 3,
        testID: 101,
        questionType: 'textAnswer',
        content: 'Explain the significance of fire temples in Zoroastrianism.',
      },
      {
        id: 4,
        testID: 102,
        questionType: 'choiceAnswer',
        content: 'Who was the first Indian Commander-in-Chief (C-in-C) of the Indian Army?',
        answers: [
          { id: 1, content: 'Gen. K.M. Cariappa', isCorrect: true },
          { id: 2, content: 'Vice-Admiral R.D. Katari', isCorrect: false },
          { id: 3, content: 'Rajendra Singhji', isCorrect: false },
          { id: 4, content: 'None of the above', isCorrect: false }
        ]
      },
      {
        id: 5,
        testID: 102,
        questionType: 'textAnswer',
        content: 'Describe the process of galvanizing iron.',
      }
    ];
  
    constructor(
        private route: ActivatedRoute,
        private testService: TestService,
        private Que
    ) {
      this.totalSteps = this.steps.length;
      this.maxScore = this.steps.filter(step => step.questionType === 'choiceAnswer').length * 20;
    
    }
    ngOnInit(): void {
        this.route.paramMap.subscribe(params => {
            this.code = params.get('code'); // Nếu bạn đặt tham số là 'id'
          });
        
        this.testService.findTestByCode(this.code).then(
            res => {
                this.title = res.title;
                console.log(res);
                
            }
        );
        
    }
    // Hiển thị câu hỏi hiện tại
    getCurrentStep() {
      return this.steps[this.now];
    }
  
    // Xác thực xem có chọn đáp án hoặc nhập câu trả lời không
    validate(stepId: number): boolean {
      const currentStep = this.steps[stepId - 1];
      if (currentStep.questionType === 'choiceAnswer' && !this.selectedOptions[stepId]) {
        this.errorMsg = 'Please select an answer!';
        return false;
      } else if (currentStep.questionType === 'textAnswer' && !this.textAnswers[stepId]) {
        this.errorMsg = 'Please provide an answer!';
        return false;
      }
      this.errorMsg = ''; // Reset lỗi nếu có
      return true;
    }
  
    // Chuyển đến câu tiếp theo
    nextStep() {
      if (this.validate(this.now + 1)) {
        this.now = this.now + 1;
      }
      if (this.now === this.totalSteps) {
        this.calculateScore();
      }
    }
  
    // Trở lại câu trước
    prevStep() {
      if (this.now > 0) {
        this.now = this.now - 1;
      }
    }
  
    // Tính điểm dựa trên các câu trả lời đúng cho các câu hỏi có đáp án
    calculateScore() {
      this.userScore = 0;
      this.steps.forEach((step) => {
        if (step.questionType === 'choiceAnswer') {
          const selectedAnswer = this.selectedOptions[step.id];
          const correctAnswer = step.answers.find(answer => answer.isCorrect);
          if (selectedAnswer === correctAnswer?.content) {
            this.userScore += 20; // Mỗi câu đúng được 20 điểm
          }
        }
      });
      this.showResultPage = true;
    }
  
    // Phương thức chọn radio option
    onOptionSelected(stepId: number, option: string) {
      this.selectedOptions[stepId] = option;
    }
  
    // Phương thức nhập câu trả lời cho câu hỏi textAnswer
    onTextAnswerChange(stepId: number, answer: string) {
      this.textAnswers[stepId] = answer;
    }
  
    // Kiểm tra xem kết quả có đạt không
    isPassed() {
      return this.userScore >= this.passingScore;
    }
}
